        % one path is found find a random point in its path
        obs_index = round(rand*(size(path,1)-1)) + 1;
        obs_pos = path(obs_index, :);
        % make at an obstacle at that point that part
        prev_CS = CS;
        CS = addObstacle(CS, obs_pos, obs_size);
        %make sure the start and goal aren't covered
        while ((CS(start(1),start(2)) == 1) || (CS(goal(1),goal(2)) == 1))
            CS = prev_CS;
            obs_index = round(rand*(size(path,1)-1)) + 1;
            obs_pos = path(obs_index, :);
            CS = addObstacle(CS, obs_pos, obs_size);
        end
        % add path
        path_list{i} = path;
        i = i + 1;
        prev_path = path;
    else
        % if no path was found then remove obstacle and find a new place to put one
        obs_index = round(rand*(size(prev_path,1)-1)) + 1;
        obs_pos = prev_path(obs_index, :);
        % make at an obstacle at that point that part
        CS = prev_CS;
        CS = addObstacle(CS, obs_pos, obs_size);
    end
end


