%adds an obstacle to the CS of any size
function CS = addObstacle(CS, pos, obs_size)
    %make sure obstacle size is an odd number
    if(mod(obs_size,2) == 0)
        obs_size = obs_size + 1;
    end
    %add obstacle
    for i = 0:obs_size-1
        for j = 0:obs_size-1
            ii = min(max(pos(1) + i - (obs_size-1)/2,1),size(CS,1));
            jj = min(max(pos(2) + j - (obs_size-1)/2,1),size(CS,2));
            CS(ii,jj) = 1;
        end
    end
return;
end


function ObsList = GenerateRandomObs(num, types, Map)
    Point.x = rand*(Map.size_x - Map.obs_size/2) + Map.obs_size/2;
    Point.y = rand*(Map.size_y - Map.obs_size/2) + Map.obs_size/2;
    PointList(1) = Point;
    if (types(1) == 4) %circle
            ObsList(1) = CreateConvexObs(Point, Map.obs_size, 0, 0, Map.circle_res);
    elseif (types(1) == 5) %circle
            ObsList(1) = CreateConvexObs(Point, Map.obs_size, round(rand*359), round(rand*359), Map.circle_res);
    else
            ObsList(1) = CreateObs(Point, types(1), Map.obs_size);
    end
    %Get 'num' of random points
    for i = 2:num
        
        %Test each point that is about to be added  whether is it a certain tolerance distance away from each point
        while(~pointValid(Point, PointList, Map.obs_size))
            Point.x = rand*(Map.size_x - Map.obs_size/2) + Map.obs_size/2;
            Point.y = rand*(Map.size_y - Map.obs_size/2) + Map.obs_size/2;
        end
        PointList(i) = Point;
        if (types(i) == 4) %circle
            ObsList(i) = CreateConvexObs(Point, Map.obs_size, 0, 0, Map.circle_res);
        elseif (types(i) == 5) %circle
            ObsList(i) = CreateConvexObs(Point, Map.obs_size, round(rand*359), round(rand*359), Map.circle_res);
        else
            
            ObsList(i) = CreateObs(Point, types(i), Map.obs_size);
        end
    end
return;
end

function LineList = PushableLines(ObsList, point, tolerance, num_of_lines)
    %Intialise all 8 potential lines using the passed in point and a second calculated point using the  direction
    LineList = [];
    i = 1;
    inside_Obs = false;
    while ((i <= length(ObsList)) && (inside_Obs == false))
        poly = ObsList(i);
        inside_Obs = inpolygon(point.x, point.y, poly.lines_x, poly.lines_y);
        i = i + 1;
    end
    if (inside_Obs == false)
        for i = 0:2*pi/num_of_lines:2*pi-2*pi/num_of_lines
            PushDir.Point1_x = point.x;
            PushDir.Point1_y = point.y;
            PushDir.Point2_x = point.x + cos(i)*tolerance;
            PushDir.Point2_y = point.y + sin(i)*tolerance;
            if (~LineIntersectObsList(PushDir, ObsList))
            	LineList = [LineList, i];
            end
        end
    end
end

function is_valid = pointValid(Point, PointList, tolerance)
    is_valid = true;
    i = 1;
    while ((i <= length(PointList)) && (is_valid == true))
        curr_Point = PointList(i);
        if (sqrt((Point.x - curr_Point.x)^2 + (Point.y - curr_Point.y)^2) < tolerance)
            is_valid = false;
        end
        i = i+1;
    end
return;
end

function intersect = LineIntersectObsList(Line, ObsList)
    intersect = false;
    i = 1;
    while ((i <= length(ObsList)) && (intersect == false))
        if(LineIntersectObs(Line, ObsList(i)))
            intersect = true;
        end
        i = i + 1;
    end
return;
end

function intersect = LineIntersectObs(Line, Obs)
    intersect = false;
    i = 1;
    while ((i <= length(Obs.lines_x)-1) && (intersect == false))
        ObsLine.Point1_x = Obs.lines_x(i);
        ObsLine.Point1_y = Obs.lines_y(i);
        ObsLine.Point2_x = Obs.lines_x(i+1);
        ObsLine.Point2_y = Obs.lines_y(i+1);
        if(LinesIntersect(Line, ObsLine))
            intersect = true;
        end
        i = i + 1;
    end
return;
end

function intersect = LinesIntersect(Line1, Line2)
    x1 = Line1.Point1_x;
    x2 = Line1.Point2_x;
    x3 = Line2.Point1_x;
    x4 = Line2.Point2_x;
    y1 = Line1.Point1_y;
    y2 = Line1.Point2_y;
    y3 = Line2.Point1_y;
    y4 = Line2.Point2_y;
    diff_x = x1 - x3;
    diff_y = y1 - y3;
    denominator = (x4 - x3).*(y1 - y2) - (x1 - x2).*(y4 - y3);
    if (denominator ~= 0)
        t1 = ((y3 - y4).*diff_x + (x4 - x3).*diff_y)/denominator;
        t2 = ((y1 - y2).*diff_x + (x2 - x1).*diff_y)/denominator;
        if ((t1 <= 1) && (t1 >= 0) && (t2 <= 1) && (t2 >= 0))
            intersect = true;
        else
            intersect = false;
        end
    else
        intersect = false;
    end    
return;
end

function Obs = CreateObs(Point, type, obs_size)
    %Reference lab view code to create square, pentagon, triangle, hexagon obstacles as list of lines
    Obs = [];
    obs_radius = obs_size/2;
    if (type == 0) %triangle
        Obs.lines_x(1) = Point.x;
        Obs.lines_y(1) = Point.y + obs_radius;
        Obs.lines_x(2) = Point.x + cos(30*(pi/180))*obs_radius;
        Obs.lines_y(2) = Point.y - sin(30*(pi/180))*obs_radius;
        Obs.lines_x(3) = Point.x - cos(30*(pi/180))*obs_radius;
        Obs.lines_y(3) = Point.y - sin(30*(pi/180))*obs_radius;
        Obs.lines_x(4) = Obs.lines_x(1);
        Obs.lines_y(4) = Obs.lines_y(1);
    elseif (type == 1) %square
        Obs.lines_x(1) = Point.x + obs_radius;
        Obs.lines_y(1) = Point.y + obs_radius;
        Obs.lines_x(2) = Point.x + obs_radius;
        Obs.lines_y(2) = Point.y - obs_radius;
        Obs.lines_x(3) = Point.x - obs_radius;
        Obs.lines_y(3) = Point.y - obs_radius;
        Obs.lines_x(4) = Point.x - obs_radius;
        Obs.lines_y(4) = Point.y + obs_radius;
        Obs.lines_x(5) = Obs.lines_x(1);
        Obs.lines_y(5) = Obs.lines_y(1);
    elseif (type == 2) %pentagon 
        Obs.lines_x(1) = Point.x;
        Obs.lines_y(1) = Point.y + obs_radius;
        Obs.lines_x(2) = Point.x + cos(18*(pi/180))*obs_radius;
        Obs.lines_y(2) = Point.y + sin(18*(pi/180))*obs_radius;
        Obs.lines_x(3) = Point.x + cos(54*(pi/180))*obs_radius;
        Obs.lines_y(3) = Point.y - sin(54*(pi/180))*obs_radius;
        Obs.lines_x(4) = Point.x - cos(54*(pi/180))*obs_radius;
        Obs.lines_y(4) = Point.y - sin(54*(pi/180))*obs_radius;
        Obs.lines_x(5) = Point.x - cos(18*(pi/180))*obs_radius;
        Obs.lines_y(5) = Point.y + sin(18*(pi/180))*obs_radius;
        Obs.lines_x(6) = Obs.lines_x(1);
        Obs.lines_y(6) = Obs.lines_y(1);
    elseif (type == 3) %hexagon
        Obs.lines_x(1) = Point.x + obs_radius;
        Obs.lines_y(1) = Point.y;
        Obs.lines_x(2) = Point.x + cos(60*(pi/180))*obs_radius;
        Obs.lines_y(2) = Point.y - sin(60*(pi/180))*obs_radius;
        Obs.lines_x(3) = Point.x - cos(60*(pi/180))*obs_radius;
        Obs.lines_y(3) = Point.y - sin(60*(pi/180))*obs_radius;
        Obs.lines_x(4) = Point.x - obs_radius;
        Obs.lines_y(4) = Point.y;
        Obs.lines_x(5) = Point.x - cos(60*(pi/180))*obs_radius;
        Obs.lines_y(5) = Point.y + sin(60*(pi/180))*obs_radius;
        Obs.lines_x(6) = Point.x + cos(60*(pi/180))*obs_radius;
        Obs.lines_y(6) = Point.y + sin(60*(pi/180))*obs_radius;
        Obs.lines_x(7) = Obs.lines_x(1);
        Obs.lines_y(7) = Obs.lines_y(1);
    end

return;
end

function Obs = CreateConvexObs(Point, obs_size, initial_angle, final_angle, res)
    %angels represent the cutout section of the circle to make it convex
    %(in degrees and must be a whole number)
    obs_radius = obs_size/2;
    Obs = [];
    if (initial_angle == final_angle)
        for i = 0:res:359
            Obs.lines_x(i/res+1) = Point.x + obs_radius*cos(i*pi/180);
            Obs.lines_y(i/res+1) = Point.y + obs_radius*sin(i*pi/180);
        end
    else
        first_time = true;
        k = 0;
        for i = 0:res:359
            if (initial_angle < final_angle)
                if (i >= initial_angle) && (i <= final_angle)
                    if (first_time == true)
                        Obs.lines_x(i/res+1) = Point.x;
                        Obs.lines_y(i/res+1) = Point.y;
                        first_time = false;
                    else
                        k = k + 1;
                    end
                else
                    Obs.lines_x(i/res+1-k) = Point.x + obs_radius*cos(i*pi/180);
                    Obs.lines_y(i/res+1-k) = Point.y + obs_radius*sin(i*pi/180);
                end
            else
                if (i >= initial_angle) || (i <= final_angle)
                    if (first_time == true)
                        Obs.lines_x(i/res+1) = Point.x;
                        Obs.lines_y(i/res+1) = Point.y;
                        first_time = false;
                    else
                        k = k + 1;
                    end
                else
                    Obs.lines_x(i/res+1-k) = Point.x + obs_radius*cos(i*pi/180);
                    Obs.lines_y(i/res+1-k) = Point.y + obs_radius*sin(i*pi/180);
                end
            end
        end
    end
    Obs.lines_x(end+1) = Obs.lines_x(1);
    Obs.lines_y(end+1) = Obs.lines_y(1);
return;
end

