function incr = findIncrement(angle, x, y, xmax, ymax)
    incr(1) = round(sin(angle));
    incr(2) = -round(cos(angle));
    if ((incr(1) < 0) && (y == 1))
        incr(1) = 0;
    end
    if ((incr(1) > 0) && (y == ymax))
        incr(1) = 0;
    end
    if ((incr(2) < 0) && (x == 1))
        incr(2) = 0;
    end
    if ((incr(2) > 0) && (x == xmax))
        incr(2) = 0;
    end
return;
end
